import React, { useState } from 'react';
import LangflowClient from './LangflowClient';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from '@chatscope/chat-ui-kit-react';
function Chat() {
    const [typing, setTyping] = useState(false);

    const [messages, setMessages] = useState([
        {
            message: "Ol치! Testando...",
            user: "SamsAI",
            direction: "ingoing",
        }
    ])

    const handleSend = async (message) => {
        const newMessage = {
            message: message,
            user: "user",
            direction: "outgoing",
        }

        const newMessages = [...messages, newMessage]; //Todas as mensagens antigas + a nova mensagem enviada

        //Atualiza o status da mensagem
        setMessages(newMessages);

        //Indicador de que a IA est치 respondend

        //Processa a mensagem (envia e exibe a resposta)

    }

    //Precisa fixar o Input
    return (
        <div>
            <MainContainer>
                <ChatContainer>
                    <MessageList>
                        {messages.map((message, i) => {
                            return <Message key = {i} model = {message}/>
                        })}
                    </MessageList>
                    <MessageInput placeholder='Escreva uma mensagem...' onSend={handleSend}/>
                </ChatContainer>
            </MainContainer>
        </div>
    );
}

export default Chat;
